import React, { useState, useEffect, useContext } from 'react';

const EmployeeProductCard = ({ drink, onClick, isSelected }) => {

    return (
        <div
            className={`w-full h-full p-4 border rounded m-4 cursor-pointer hover:bg-slate-300 flex flex-col items-center ${
            isSelected ? 'bg-slate-300' : ''
            }`}
            onClick={() => onClick(drink)}
        >
            <div className="flex mt-5">
            <img
                src={`drink_default.png`}
                alt={drink.name}
                className="w-32 h-32 object-cover rounded"
            />
            </div>
            <h2 className="text-xl font-semibold m-4">{drink.name}</h2>
            <p>Price: ${drink.price.toFixed(2)}</p>
        </div>
  );
};

export default EmployeeProductCard;
