import React, { useState, useEffect, useContext } from 'react';
import CustomizeOrderComponent from './CustomComponent';
import ProductCategory from './ProductComponent';
import OrderComponent from './OrderComponent';
import { OrderProvider } from './OrderContext';
import '../HighContrast.css';

function EmployeeView() {

    const [isHighContrast, setIsHighContrast] = useState(false);

    return (
        <OrderProvider>
            <div className={`EmployeeView ${isHighContrast ? 'high-contrast' : ''}`}>
                <div className="absolute right-6 top-20 flex gap-3">
                    <button 
                        className="bg-white text-black font-semibold border-black border-2 hover:bg-gray-200 hover:text-black round p-1 px-2 text-sm rounded-xl hover:shadow-lg"
                        onClick={() => setIsHighContrast(!isHighContrast)}
                   >High Contrast
                    </button>
                </div>
                <div className={`fixed w-full h-full EmployeeView ${isHighContrast ? 'high-contrast' : ''}`}>
                    <div className='flex h-full'>
                        <div id="Order" className='h-full w-1/3 border-r'>
                            <OrderComponent/>
                        </div>
                        <div id="Product" className='w-full flex flex-col'>
                            <div id="ProductCategory" className='flex-1 overflow-y-auto h-1/2'>
                                <ProductCategory
                                    isHighContrast = {isHighContrast}
                                />
                            </div>
                            <div 
                                id="ProductCustomization" 
                                className={`sticky bottom-0 h-1/3 p-4 bg-white m-4 border-t flex EmployeeView ${isHighContrast ? 'high-contrast' : ''}`}>
                                <CustomizeOrderComponent
                                    isHighContrast = {isHighContrast}
                                />
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        </OrderProvider> 
    );
}

export default EmployeeView;
