import React, { useState, useEffect } from 'react';
import config from './config'; 
import DrinkCard from './DrinkCard';

const CustomerOrder = () => {
  const [selectedCategory, setSelectedCategory] = useState('All');

  const drinks = [
    {
      name: 'Classic Milk Tea',
      category: 'Milk',
      price: 4.99,
      image: 'classic-milk-tea.jpg',
    },
    {
      name: 'Taro Milk Tea',
      category: 'Milk',
      price: 5.49,
      image: 'taro-milk-tea.jpg',
    },
    {
      name: 'Strawberry Yogurt Smoothie',
      category: 'Yogurt',
      price: 5.99,
      image: 'strawberry-yogurt-smoothie.jpg',
    },
    {
      name: 'Jasmine Green Tea',
      category: 'Tea',
      price: 3.99,
      image: 'jasmine-green-tea.jpg',
    },
    // Add more drinks as needed
  ];

  const filterDrinksByCategory = (category) => {
    if (category === 'All') {
      return drinks;
    }
    return drinks.filter((drink) => drink.category === category);
  };

  return (
    <div>
      <div className="my-4">
        <h2 className="text-2xl font-semibold">Boba Menu</h2>
      </div>

      <div className="my-4 flex space-x-4">
        <button onClick={() => setSelectedCategory('All')} className={selectedCategory === 'All' ? 'bg-blue-500 text-white px-4 py-2 rounded' : 'bg-gray-300 px-4 py-2 rounded'}>
          All
        </button>
        <button onClick={() => setSelectedCategory('Milk')} className={selectedCategory === 'Milk' ? 'bg-blue-500 text-white px-4 py-2 rounded' : 'bg-gray-300 px-4 py-2 rounded'}>
          Milk
        </button>
        <button onClick={() => setSelectedCategory('Yogurt')} className={selectedCategory === 'Yogurt' ? 'bg-blue-500 text-white px-4 py-2 rounded' : 'bg-gray-300 px-4 py-2 rounded'}>
          Yogurt
        </button>
        <button onClick={() => setSelectedCategory('Tea')} className={selectedCategory === 'Tea' ? 'bg-blue-500 text-white px-4 py-2 rounded' : 'bg-gray-300 px-4 py-2 rounded'}>
          Tea
        </button>
      </div>

      <div className="grid grid-cols-2 gap-4">
        {filterDrinksByCategory(selectedCategory).map((drink, index) => (
          <DrinkCard key={index} drink={drink} />
        ))}
      </div>
    </div>
  );
};

export default CustomerOrder;
