import React, { useState, useEffect } from 'react';
const api ={
    key: "773b061018c2fcc2f5403e0ed3bae00f",
    base: "https://api.openweathermap.org/data/2.5/",
}

const WeatherApi = () => {
    const [weather, setWeather] = useState({});

    useEffect(() => {
        fetch(`${api.base}weather?q=College Station&units=metric&APPID=${api.key}`)
            .then(res => res.json())
            .then(result => {
                setWeather(result);
            });
    }, []);

    return (
        <div>
            <p>Place: {weather.name}</p>
            <p>Temperature: {Math.round(weather.main?.temp)}°C</p>
            <p>Temperature_min: {Math.round(weather.main?.temp_min)}°C</p>
            <p>Temperature_max: {Math.round(weather.main?.temp_max)}°C</p>
            <p>Weather: {weather.weather?.[0]?.main}</p>
            <p>Description: {weather.weather?.[0]?.description}</p>
            weather.
        </div>
    );
};

export default WeatherApi;
