import React, { useState, useEffect } from 'react';
import config from './config'; 

const DrinkCard = ({ drink }) => {
  const [expanded, setExpanded] = useState(false);
  const [sweetnessLevel, setSweetnessLevel] = useState('Regular');
  const [iceLevel, setIceLevel] = useState('Regular');
  const [cart, setCart] = useState([]);

  const toggleExpansion = () => {
    setExpanded(!expanded);
  };

  const addToCart = () => {
    const customDrink = { ...drink, sweetness: sweetnessLevel, ice: iceLevel };
    setCart([...cart, customDrink]);
    // You can also save the cart in your application's state or send it to a server for processing.
  };

  return (
    <div className="p-4 border shadow-md m-4 relative">
      <h2 className="text-xl font-semibold">{drink.name}</h2>
      <p>Price: ${drink.price.toFixed(2)}</p>
      <img
        src={drink.image}
        alt={drink.name}
        className="w-32 h-32 object-cover rounded-md mt-2 cursor-pointer"
        onClick={toggleExpansion}
      />
      <div className={`mt-2 ${expanded ? 'block' : 'hidden'} absolute top-0 left-0 w-full h-full bg-white bg-opacity-90 blur`}>
        <div className="p-4">
          <h3 className="text-lg font-semibold mb-2">Customize Your Drink</h3>
          <div className="mb-2">
            <label className="block">Sweetness Level</label>
            <select
              value={sweetnessLevel}
              onChange={(e) => setSweetnessLevel(e.target.value)}
              className="w-full p-2 border rounded"
            >
              <option value="Regular">Regular</option>
              <option value="Less Sweet">Less Sweet</option>
              <option value="Half Sweet">Half Sweet</option>
              <option value="Quarter Sweet">Quarter Sweet</option>
            </select>
          </div>
          <div className="mb-2">
            <label className="block">Ice Level</label>
            <select
              value={iceLevel}
              onChange={(e) => setIceLevel(e.target.value)}
              className="w-full p-2 border rounded"
            >
              <option value="Regular">Regular</option>
              <option value="Less Ice">Less Ice</option>
              <option value="No Ice">No Ice</option>
            </select>
          </div>
          <button onClick={addToCart} className="bg-blue-500 text-white px-4 py-2 rounded">
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default DrinkCard;
