import React, { useState, useEffect, useContext } from 'react';
import { useParams, Link } from 'react-router-dom';
import config from './config'; 
import { CartContext } from './CartContext'; // Adjust the path as needed

const Customization = () => {

    const { addToCart } = useContext(CartContext);

    const { drinkId } = useParams();
    const [drink, setDrink] = useState(null);
    const [iceLevel, setIceLevel] = useState('Regular');
    const [sweetnessLevel, setSweetnessLevel] = useState('Regular');

    const handleSubmit = () => {
        console.log("Submitting customization")
        const customizedDrink = {
        ...drink,
        iceLevel,
        sweetnessLevel,
        };
        addToCart(customizedDrink);
        // Redirect or show confirmation as needed
    };

    useEffect(() => {
        const fetchDrink = () => {
            fetch(`${config.REACT_APP_BACKEND_URL}/api/product/${drinkId}`)
                .then(response => response.json())
                .then(data => {
                    setDrink(data);
                })
                .catch(error => {
                    console.error("There was an error fetching the data", error);
                });
        };
        fetchDrink();
    }, [drinkId]);

    if (!drink) {
        return <div>Loading...</div>;
    }

    return (
        <div className="flex justify-center">             
        <div className="p-5 w-4/5">
            <div className="text-xl flex justify-start font-bold p-5 rounded-xl shadow-md">
                <Link to="/order">Return to Order</Link>
                <div></div>
            </div>
            {/* <div className="text-xl font-bold mb-4 p-10">Customize Your Drink</div> */}
            <div className="border-b border-gray-300 mb-4">
                <div className="flex justify-center items-center pt-4 w-full">
                </div>
                <h1 className="text-2xl font-semibold">{drink.name}</h1>
                <h2 className="text-lg text-gray-600">${drink.price}</h2>
            </div>
            <div className="mb-4">
                <label className="block text-md font-medium text-gray-700 mb-2">Ice Level</label>
                <select className="block w-full border border-gray-300 p-2 rounded" value={iceLevel} onChange={(e) => setIceLevel(e.target.value)}>
                    <option value="None">No Ice</option>
                    <option value="Less">Less Ice</option>
                    <option value="Regular">Regular Ice</option>
                    <option value="Extra">Extra Ice</option>
                </select>
            </div>
            <div className="mb-4">
                <label className="block text-md font-medium text-gray-700 mb-2">Sweetness Level</label>
                <select className="block w-full border border-gray-300 p-2 rounded" value={sweetnessLevel} onChange={(e) => setSweetnessLevel(e.target.value)}>
                    <option value="0%">0% (Unsweetened)</option>
                    <option value="25%">25% (Less Sweet)</option>
                    <option value="50%">50% (Half)</option>
                    <option value="75%">75% (Less Sugar)</option>
                    <option value="100%">100% (Regular)</option>
                </select>
            </div>
            <button onClick={handleSubmit} className="bg-black text-white font-bold py-2 px-4 rounded-full">Submit</button>
            </div>
        </div>
    );
}

export default Customization;
