import React, { useState, useEffect } from 'react';
import config from './config'; 

function InventoryList() {
    const [items, setItems] = useState([]);
    const [loading, setLoading] = useState(true);
    const [editedItems, setEditedItems] = useState({});

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = () => {
        setLoading(true);
        fetch(`${config.REACT_APP_BACKEND_URL}/api/inventory`)
            .then(response => response.json())
            .then(data => {
                setItems(data);
                setLoading(false);
            })
            .catch(error => {
                console.error("There was an error fetching the data", error);
                setLoading(false);
            });
    };

    const handleQuantityChange = (event, id) => {
        const newQuantity = parseInt(event.target.value, 10);
        if (!isNaN(newQuantity)) {
            setEditedItems(prevItems => ({
                ...prevItems,
                [id]: { ...prevItems[id], quantity: newQuantity },
            }));
        }
    };

    const saveChanges = () => {
        const updatePromises = Object.entries(editedItems).map(([id, data]) =>
            fetch(`${process.env.REACT_APP_BACKEND_URL}/api/inventory/${id}`, {
                method: 'PUT',//HERE is where calling backend API
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(data),
            })
        );

        Promise.all(updatePromises)
            .then(() => {
                fetchData(); 
                setEditedItems({}); 
            })
            .catch(error => console.error('There was an error updating the items', error));
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h2>Inventory Items</h2>
            <button onClick={saveChanges} disabled={loading || Object.keys(editedItems).length === 0}>
                Save Changes
            </button>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Quantity</th>
                        <th>Cost</th>
                        <th>Price</th>
                        <th>Is Addon?</th>
                        <th>Update Quantity</th>
                    </tr>
                </thead>
                <tbody>
                    {items.map(item => (
                        <tr key={item.id}>
                            <td>{item.id}</td>
                            <td>{item.name}</td>
                            <td>
                                <input
                                    type="number"
                                    value={editedItems[item.id]?.quantity ?? item.quantity}
                                    onChange={(event) => handleQuantityChange(event, item.id)}
                                />
                            </td>
                            <td>{item.cost}</td>
                            <td>{item.price}</td>
                            <td>{item.isaddon ? 'Yes' : 'No'}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default InventoryList;
