// In ManagerView.js

import React, { useState } from 'react';
import InventoryList from './InventoryList';
import ManagerMenu from './ManagerMenu';

const ManagerView = () => {
    const [restockItems, setRestockItems] = useState([]);

    const fetchRestockList = async () => {
        try {
            const response = await fetch('/api/inventory/restock'); // Update this URL based on your API endpoint
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();
            setRestockItems(data);
        } catch (error) {
            console.error('Error fetching restock list:', error);
        }
    };

    return (
        <div>
            <div className="my-4">
                <h2 className="text-2xl font-bold flex justify-center">MENU</h2>
                <div>
                    <ManagerMenu />
                </div>
                <h2 className="text-2xl font-bold flex justify-center">INVENTORY</h2>
                <div className="">
                    <button onClick={fetchRestockList}>Fetch Restock List</button>
                    <InventoryList inventoryItems={restockItems} />
                </div>
            </div>
        </div>
    )
}

export default ManagerView;
