import React, { useState } from 'react';
import EmployeeProductCard from './EmployeeProductCard';
import CustomizationCard from './CustomizationCard';

const CustomizeOrderComponent = () => {
    const [selectedIce, setSelectedIce] = useState('Regular');
    const [selectedSweetness, setSelectedSweetness] = useState('100');

    const handleCustomizationCardClick = (category, type) => {
        if (category === "Ice"){
            setSelectedIce(type);
        } else {
            setSelectedSweetness(type);
        }
    };

    const handleOrderButtonClick = () => {};

    const generateCustomizationCards = (category, types) => {
        return types.map((type, index) => (
            <CustomizationCard 
                key={index} 
                category={category} 
                type={type}
                onClick={handleCustomizationCardClick}
                isSelected={selectedIce === type || selectedSweetness === type}
            />
          ));
    }

    return (
        <div className="flex gap-7">
            <div id="CustomizeIce" className="w-1/3 border-r">
                <h2 className='font-semibold'>ICE LEVEL</h2>
                <div className='grid grid-cols-1 md:grid-cols-2 h-full'>
                    {generateCustomizationCards('Ice', ['Extra', 'Regular', 'Less', 'No'])}
                </div>
            </div>
            <div id="CustomizeSweetness" className="w-1/2">
                <h2 className='font-semibold'>SWEETNESS LEVEL</h2>
                <div className='grid grid-cols-1 md:grid-cols-3 h-full'>
                    {generateCustomizationCards('Sweetness', ['100', '75', '50', '25', '0'])}
                </div>
            </div>
            <div id="OrderButtons" className='fixed bottom-10 right-10'>
                <button 
                    className='font-semibold border p-3 rounded-xl hover:shadow-md bg-slate-100'
                    onClick={handleOrderButtonClick}
                >Add to Order</button>
            </div>
        </div>
    );
};

export default CustomizeOrderComponent;
