import React from 'react';

// styling
import './App.css';

// pages
import LandingPage from './LandingPage'
import Menu from './Menu';
import CustomerOrder from './CustomerOrder';
import EmployeeView from './EmployeeView';
import Customization from './Customization';
import Login from './Login';
import ProtectedRoute from './ProtectedRoute';
import Logout from './Logout';
import ManagerView from './ManagerView';

import { CartProvider } from './CartContext';

import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { useAuth0 } from '@auth0/auth0-react';
import LogoutButton from './Logout';
import LoginButton from './Login';

function App() {
  const { user, isAuthenticated, isLoading } = useAuth0();

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <CartProvider>
    <div className="App">
    <header className="bg-tiger-gray p-7">
      <div className="flex justify-center text-white w-full">
        <h1 className="font-bold text-yellow-200 text-3xl">TIGER SUGAR</h1>
      </div>
      {isAuthenticated && (<div className="absolute left-6 top-5">
        <div className="flex text-white">
          <img src={user.picture} alt="Profile" className="h-12 w-12 rounded-full mr-4" />
          <div>
            <p className="font-semibold">{user.name}</p>
            <p className="text-sm">{user.email}</p>
          </div>
        </div>
      </div>)}
      <div className="absolute bg-white right-6 top-5">
      <LoginButton>
        <Button className="login-button">Login</Button>
      </LoginButton>
      <LogoutButton>
        <Button className="logout-button">Logout</Button>
      </LogoutButton>
      </div>
    </header>


      <Router>
        <Routes>
          <Route path="/" element={<LandingPage />} />
          <Route path="/login" element={<Login />} />
          <Route path="/order" element={<CustomerOrder />} />
          <Route path="/menu" element={<Menu />} />
          <Route path="/manager" element={
            <ProtectedRoute>
              <ManagerView />
            </ProtectedRoute>} />
          {/* TODO - when finished, protect with protected route */}
          <Route path="/employee" element={<EmployeeView />} />
          <Route path="/customization/:drinkId" element={<Customization/>} />
          <Route path="/logout" element={<Logout />} />
        </Routes>
      </Router>
    </div>
    </CartProvider>
  );
}

export default App;
