import React, { useEffect, useState } from 'react';

const ManagerView = () => {
    const [excessInventory, setExcessInventory] = useState([]);

    useEffect(() => {
        fetchExcessInventory();
    }, []); // Empty dependency array to run only once on component mount

    const fetchExcessInventory = async () => {
        try {
            const response = await fetch('/api/inventory/excess');
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();
            setExcessInventory(data);
        } catch (error) {
            console.error('Error fetching excess inventory:', error);
        }
    };

    return (
        <div>
            <h2>Excess Inventory List</h2>
            <ul>
                {excessInventory.map(item => (
                    <li key={item.id}>{item.name} - {item.quantity}</li>
                ))}
            </ul>
        </div>
    );
};

export default ManagerView;
