const CustomizationCard = ({ category, type, onClick, isSelected, isHighContrast }) => {

    const imageName = `${type.toLowerCase()}_${category.toLowerCase()}.png`;
    let caption;

    if (category === 'Sweetness'){
        caption = `${type}% ${category}`;
    } else {
        caption = `${type} ${category}`;
    }

    return (
      <div className='grid grid-cols-1 md:grid-cols-2 cursor-pointer items-center'>
        <div className="flex w-full mt-2 mr-2 justify-end">
          <img
            src={`/customization_images/${imageName}`}
            alt={caption}
            className={`w-full h-full border rounded-full hover:bg-slate-300 hover:shadow-lg 
            ${isHighContrast ? 'bg-white hover:bg-lime-400' : ''}
            ${isSelected ? 
              (isHighContrast? 'border-lime-400 shadow-lg border-4 bg-lime-400' : 'bg-slate-300 shadow-lg border-4 border-yellow-500') 
            : ''}
            `}
            onClick={() => onClick(category, type)}
          />
        </div>
        <h2 className="ml-2 text-sm font-semibold">{caption}</h2>
      </div>
  );
};

export default CustomizationCard;
