import { Link } from 'react-router-dom';

const DrinkCard = ({ drink }) => {
  return (
    <Link to={`/customization/${drink.id}`} className="no-underline text-black">
      <div className="p-4 border shadow-md rounded m-4 relative cursor-pointer hover:shadow-xl">
        <h2 className="text-xl font-semibold">{drink.name}</h2>
        <p>Price: ${drink.price.toFixed(2)}</p>
        <div className="flex justify-center items-center w-full">
          <img
            src={`${drink.id}.jpg`}
            alt={drink.name}
            className="w-32 h-32 object-cover rounded mt-2"
          />
        </div>
      </div>
    </Link>
  );
};

export default DrinkCard;
