import React, { useState } from 'react';

// styling
import './App.css';

// pages
import LandingPage from './LandingPage'
import Menu from './MenuComponents/Menu';
import CustomerOrder from './CustomerOrder';
import EmployeeView from './EmployeeComponents/EmployeeView';
import Customization from './Customization';
import Login from './Login';
import ProtectedRoute from './ProtectedRoute';
import Logout from './Logout';
import ManagerView from './ManagerView';
import GoogleTranslate from './Translate.js';

import { CartProvider } from './CartContext';

import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import { useAuth0 } from '@auth0/auth0-react';
import LogoutButton from './Logout';
import LoginButton from './Login';

function App() {
  const { user, isAuthenticated, isLoading } = useAuth0();

  const [isHighContrast, setIsHighContrast] = useState(false);

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <CartProvider>
          <Router>
            <div className={`${isHighContrast ? 'high-contrast' : ''}`}>
              <header className={`${isHighContrast ? 'bg-black p-7' : 'bg-tiger-gray p-7'}`}>
                <div className="flex justify-center text-white w-full">
                  <h1 className='TIGERSUGAR font-bold text-yellow-200 text-3xl'>TIGER SUGAR</h1>
                </div>
                {isAuthenticated && (<div className="absolute left-6 top-5">
                  <div className="flex text-white">
                    <img src={user.picture} alt="Profile" className="h-12 w-12 rounded-full mr-4" />
                    <div>
                      <p className="font-semibold">{user.name}</p>
                      <p className="text-sm">{user.email}</p>
                    </div>
                  </div>
                </div>)}
                <div className="fixed right-64 top-6">
                  <GoogleTranslate />
                </div>
                <div className="absolute right-6 top-5 flex gap-3">
                  <LoginButton/>
                  <LogoutButton/>
                </div>
                <div className="absolute right-6 top-20 flex gap-3">
                  <button 
                      className="bg-white text-black font-semibold border-black border-2 hover:bg-gray-200 hover:text-black round p-1 px-2 text-sm rounded-xl hover:shadow-lg"
                      onClick={() => setIsHighContrast(!isHighContrast)}
                  >High Contrast
                  </button>
                </div>

                <nav className="flex justify-center space-x-4 mt-4">
                  <Link to="/" 
                    className='LINK text-white hover:text-yellow-200 font-semibold'>HOME
                  </Link>
                  <Link to="/order" 
                    className='LINK text-white hover:text-yellow-200 font-semibold'>ORDER
                  </Link>
                  <Link to="/menu" 
                    className='LINK text-white hover:text-yellow-200 font-semibold'>MENU
                  </Link>
                  {isAuthenticated && (
                    <>
                      <Link to="/manager" 
                        className='LINK text-white hover:text-yellow-200 font-semibold'>MANAGER
                      </Link>
                      <Link to="/employee" 
                        className='LINK text-white hover:text-yellow-200 font-semibold'>EMPLOYEE
                      </Link>
                    </>
                  )}
                </nav>
              </header>
              <Routes>
                <Route path="/" element={<LandingPage/>} />
                <Route path="/login" element={<Login />} />
                <Route path="/order" 
                  element={<CustomerOrder 
                    isHighContrast={isHighContrast}
                  />} 
                />
                <Route path="/menu" element={<Menu />} />
                <Route path="/manager" element={
                  <ProtectedRoute>
                    <ManagerView />
                  </ProtectedRoute>} />
                <Route 
                  path="/employee" 
                  element={
                    <ProtectedRoute> 
                      <EmployeeView /> 
                    </ProtectedRoute>}
                />
                <Route path="/customization/:drinkId" element={<Customization/>} />
                <Route path="/logout" element={<Logout />} />
              </Routes>
            </div>
          </Router>
    </CartProvider>
  );
}

export default App;
